def print_section_header(title: str, emoji: str = "üõ∏"):
    """Print formatted section header"""
    print(f"\n{'='*80}")
    print(f"{emoji} {title.upper()} {emoji}")
    print(f"{'='*80}")

def print_subsection(title: str, emoji: str = "üîπ"):
    """Print formatted subsection"""
    print(f"\n{emoji} {title}")
    print("-" * (len(title) + 4))

